import UserAuth from "../Models/Profile.js";

export async function socketHandler(io) {
  console.log("ðŸŸ£ socketHandler attached");

  // Map to store online users { userId: [socketIds] }
  const onlineUsers = {};

  io.on("connection", (socket) => {
    console.log("âœ… Socket connected:", socket.id);

    // user register kare jab login/notification page khule
    socket.on("register", async (userId) => {
      if (!userId) return;

      if (!onlineUsers[userId]) onlineUsers[userId] = [];
      onlineUsers[userId].push(socket.id);
      console.log("ðŸŸ¢ Registered user:", userId);

      // Unread follow requests bhejna (offline notifications)
      const user = await UserAuth.findById(userId)
        .populate("followRequests.senderId", "fullName profilePic")
        .lean();

      if (user && user.followRequests.length > 0) {
        const unread = user.followRequests.filter((r) => !r.isRead);
        unread.forEach((req) => {
          io.to(socket.id).emit("receive_follow_request", {
            senderId: req.senderId?._id,
            senderName: req.senderId?.fullName || "Unknown",
            senderProfilePic:
              req.senderId?.profilePic ||
              "https://cdn-icons-png.flaticon.com/512/149/149071.png",
            message: `${req.senderId?.fullName || "Someone"} sent you a follow request.`,
            createdAt: req.createdAt,
          });
        });

        // Mark as read
        await UserAuth.updateOne(
          { _id: userId },
          { $set: { "followRequests.$[elem].isRead": true } },
          { arrayFilters: [{ "elem.isRead": false }] }
        );
      }

      console.log("ðŸŸ¢ Online Users:", Object.keys(onlineUsers));
    });

    // When a follow request is sent
    socket.on("send_follow_request", async ({ fromUserId, toUserId }) => {
      if (!fromUserId || !toUserId) return;

      const sender = await UserAuth.findById(fromUserId);
      if (!sender) return;

      // Save follow request in DB
      await UserAuth.updateOne(
        { _id: toUserId },
        {
          $push: {
            followRequests: {
              senderId: fromUserId,
              isRead: false,
              createdAt: new Date(),
            },
          },
        }
      );

      const receiverSockets = onlineUsers[toUserId];
      if (receiverSockets && receiverSockets.length > 0) {
        // Send realtime notification
        receiverSockets.forEach((sockId) => {
          io.to(sockId).emit("receive_follow_request", {
            senderId: fromUserId,
            senderName: sender.fullName,
            senderProfilePic:
              sender.profilePic ||
              "https://cdn-icons-png.flaticon.com/512/149/149071.png",
            message: `${sender.fullName} sent you a follow request.`,
            createdAt: new Date(),
          });
        });
      }
    });

    // Disconnect
    socket.on("disconnect", () => {
      console.log("âŒ Socket disconnected:", socket.id);
      for (const userId in onlineUsers) {
        onlineUsers[userId] = onlineUsers[userId].filter(
          (id) => id !== socket.id
        );
        if (onlineUsers[userId].length === 0) delete onlineUsers[userId];
      }
    });
  });
}
